import hero from "../images/hero.jpg";

const Hero = () => {
  return (
    <Section>
      <Background>
        <img src={hero} alt="background" />
      </Background>
      <Content>
        <div className="infor">
          <h2>RETAILER</h2>
          <em>
            Lorem ipsum dolor sit, amet consectetur adipisicing elit. Vitae,
            voluptatum
          </em>
          <button>ORDER NOW</button>
        </div>
      </Content>
    </Section>
  );
};

const Section = styled.section`
  height: 90vh;
  width: 100vw;
  position: relative;
  margin-bottom: 2rem;
`;

const Background = styled.div`
  height: 100%;
  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    filter: brightness(60%);
  }
`;

const Content = styled.div`
  position: absolute;
  top: 0;
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;

  .sale {
    position: relative;
    left: 10%;

    img {
      height: 70vh;
    }
    h1 {
      color: #fff;
      position: absolute;
      top: 10rem;
      left: 8vw;
      font-size: 4rem;

      span {
        font-size: 4.6rem;
      }
    }
  }
  .infor {
    position: absolute;
    top: 40%;
    right: 10%;
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    gap: 1rem;

    h2 {
      color: #f9c47f;
      font-size: 4rem;
      letter-spacing: 0.5rem;
    }
    em {
      color: #fff;
      width: 70%;
      text-align: end;
      font-size: 1.1rem;
      letter-spacing: 0.1rem;
    }
    button {
      background: #fc4958;
      padding: 1rem 2rem;
      border: 0;
      font-weight: 800;
      font-size: 1.4rem;
      letter-spacing: 0.4rem;
      outline: none;
      color: white;
      transition: all 0.35s cubic-bezier(0.215, 0.61, 0.355, 1);
      cursor: pointer;

      &:hover {
        background: #f9c74f;
      }
    }
  }
`;

export default Hero;
