import React, { useState } from 'react';
import logo from '/images/header.jpg';
import Categories from './Categories';
import items from './data';

const allCategories = ['all', ...new Set(items.map((item) => item.category))]

function App() {
  const [menuItems, setMenuItems] = useState(items)
  const [categories, setCategories] = useState(allCategories)

  const filterItems = (category) => {
    if (category === 'all') {
      setMenuItems(items)
      return
    }
    const newItems = items.filter((item) => item.category === category)
    setMenuItems(newItems)
  }
  return (
    <div className="App">
      <div className="bg">
        <img src={logo} width="100%" height="400rem" />
      </div>
      <h1
        style={{
          fontFamily: "Parisienne",
          backgroundColor: "black",
          color: "white",
          marginTop: "0px"
        }}
      >
        Restaurant Recommender
      </h1>
      <button className="btn" onClick={italianChoice}>
        Italian
      </button>
      <button className="btn" onClick={chineeseChoice}>
        Chineese
      </button>
      <button className="btn" onClick={SouthIndianChoice}>
        South Indian
      </button>
      <button className="btn" onClick={DessertChoice}>
        Desert
      </button>

      <ul>{resname}</ul>
    </div>
  );
  return (
    <main>
      <section className="menu section">
        <div className="title">
          <h2>¡Conoce Nuestro Menú!</h2>
          <div className="underline"></div>
        </div>
        <Categories categories={categories} filterItems={filterItems} />
        <Menu items={menuItems} />
      </section>
    </main>
  )
}

export default App
